import React, { Component } from "react";
import { connect } from "react-redux";
import delighters from "../common/delighters";
import {NavLink} from "react-router-dom";
import { fetchWork } from "../../actions";

class WorksDetail extends Component {
    componentDidMount() {
        const promise = new Promise((resolve) => {
            resolve(this.props.fetchWorks());
        });
        promise.then(() =>
            delighters.init()
        );
    }

    renderDetail () {
        return this.props.works.map(work => {
            return (
                <>
                    <section className="work-box">
                        <div>
                            <div>
                                <NavLink to={`/worksDetail/${work.id}`}><img src={`/img/${work.thumnail}`} alt={work.title}/></NavLink>
                                <div className="blue" data-delighter></div>
                            </div>
                        </div>
                        <ul className="fade-up-in" data-delighter>
                            <li><h2><NavLink to="/worksDetail">{work.title}</NavLink></h2></li>
                            <li>{`${work.typeOfJob} / ${work.category}`}</li>
                            <li><NavLink className="btn" to="/worksDetail">Detail →</NavLink></li>
                        </ul>
                    </section>
                </>
            )
        })
    }

    render() {
        return (
            <div className="work-detail">
                <section className="first-content">
                    <h1>METAL REVISION.INC</h1>
                    <p className="scale-up" data-delighter><img src="/img/img-works-01.png" alt="" /></p>
                </section>
                <section className="second-content">
                    <div className="container">
                        <ul className="fade-up-in" data-delighter>
                            <li></li>
                            <li>
                                <ul>
                                    <li>Date - Jun 2018</li>
                                    <li>Category - Web</li>
                                    <li>
                                        <ul>
                                            <li>HTML</li>
                                            <li>Javascript</li>
                                            <li>Wordpress</li>
                                            <li>CSS</li>
                                        </ul>
                                    </li>
                                    <li><a className="square-btn" href="https://www.agent-grow.com/" target="_blank">View</a></li>
                                </ul>
                            </li>
                        </ul>

                    </div>
                </section>
                <section className="third-content">
                    <div>
                        <img src="/img/work-detail-sample.png" alt=""/>
                        <div className="blue" data-delighter></div>
                    </div>
                </section>
                <section className="fourth-content">
                    <div className="container scale-up" data-delighter>
                        <img src="/img/work-detail-mobile.png" alt=""/>
                    </div>
                </section>
                <section className="work-list">
                    <div className="container">
                        <ul>
                            <li><a href=""></a></li>
                            <li><a href=""></a></li>
                            <li><a href=""></a></li>
                            <li><a href=""></a></li>
                            <li><a href=""></a></li>
                        </ul>
                    </div>
                </section>
                <ul className="load-overlay" data-delighter>
                    <li className="fade-out-up"></li>
                    <li className="fade-out-up"></li>
                    <li className="fade-out-up"></li>
                    <li className="fade-out-up"></li>
                    <li className="fade-out-up"></li>
                </ul>
            </div>
        );
    }
}

const mapStateToProps = (state, ownProps) => {
    return {
        works: state.works[ownProps.match.params.id]
    };
};

export default WorksDetail;
