import React, { Component } from "react";
import {connect} from "react-redux";
import {
    NavLink,
} from "react-router-dom";
import delighters from '../common/delighters';
import { fetchWorks } from "../../actions";


class Home extends Component {
    componentDidMount() {
        delighters.init();
        this.props.fetchWorks();
    }
    renderList () {
        return this.props.works.map(work => {
            return (
                <>
                <section className="work-box">
                <div>
                <NavLink to="/worksDetail"><img src="/img/img-works-01.png" alt=""/></NavLink>
                <div className="blue" data-delighter></div>
                </div>
                <ul className="fade-up-in" data-delighter>
                    <li><h2><NavLink to="/worksDetail">METAL REVISION.INC</NavLink></h2></li>
                    <li>Freelance job / Website</li>
                    <li><NavLink className="btn" to="/worksDetail">Detail →</NavLink></li>
                </ul>
            </section>
                </>
            )
        })
    }
    render() {
        return (
                <div className="home">
                    {this.renderList()}
                    <section>
                        <ul className="main-visual">
                            <li className="left">
                                <ul className="demo fade-up-in" data-delighter>
                                    <li><h1>Hello,<span className="sp-line-break">I’m Yasuhiro.</span></h1></li>
                                    <li><p>Front End developer.</p></li>
                                </ul>
                            </li>
                        </ul>
                    </section>

                    <section className="work-box">
                        <div>
                            <NavLink to="/worksDetail"><img src="/img/img-works-02.png" alt=""/></NavLink>
                            <div className="blue" data-delighter></div>
                        </div>
                        <ul className="fade-up-in" data-delighter>
                            <li><h2><NavLink to="/worksDetail">HIRATA TAKAAKI PORTFOLIO</NavLink></h2></li>
                            <li>Freelance job / Website</li>
                            <li><NavLink className="btn" to="/worksDetail">Detail →</NavLink></li>
                        </ul>
                    </section>
                    <ul className="load-overlay" data-delighter>
                        <li className="fade-out-up"></li>
                        <li className="fade-out-up"></li>
                        <li className="fade-out-up"></li>
                        <li className="fade-out-up"></li>
                        <li className="fade-out-up"></li>
                    </ul>
                </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        works: Object.values(state.works)
    }
};

export default connect(
    mapStateToProps,
    { fetchWorks }
)(Home);
