import React, { Component } from "react";
import './about.scss'
import delighters from "../common/delighters";
import ReactFullpage from "@fullpage/react-fullpage";
import {NavLink} from "react-router-dom";

class About extends Component {
    componentDidMount() {
        delighters.init();
    }
    render() {
        const Fullpage = () => (
            <ReactFullpage
                render={() => {
                    return (
                        <ReactFullpage.Wrapper>
                            <div className="about">
                                <ul>
                                    <li className="left" data-delighter>
                                        <div><img src="img/img-profile.png" alt=""/></div>
                                    </li>
                                    <li className="right" data-delighter>
                                        <div>
                                            <h2>Yasuhiro Katayama.</h2>
                                            <p>Hello!
                                                Iâ€™m Yasuhiro Katayama.I am currently studying at Cornerstone International Community College of Canada (CICCC) for Web and Mobile Development program.<br/>
                                                In Japan, I worked as a frontend engineer for two years and backend engineer for one year and 6 months. I am looking for work experience in Canada so I would be happy just to work as an intern or volunteer. I love programming and would like to learn new skills.</p>
                                            <ul>
                                                <li>
                                                    <dl>
                                                        <dt>Programing Skills</dt>
                                                        <dd>HTML & CSS</dd>
                                                        <dd>Javascript</dd>
                                                        <dd>React & React Native</dd>
                                                        <dd>Java</dd>
                                                        <dd>Kotlin</dd>
                                                        <dd>Firebase</dd>
                                                        <dd>MySQL</dd>
                                                    </dl>
                                                </li>
                                                <li>
                                                    <dl>
                                                        <dt>Design Skills</dt>
                                                        <dd>Web design</dd>
                                                        <dd>Sketch</dd>
                                                        <dd>XD</dd>
                                                        <dd>Photoshop</dd>
                                                        <dd>Illustrator</dd>
                                                    </dl>
                                                </li>
                                            </ul>
                                        </div>
                                    </li>
                                </ul>
                            </div>
                        </ReactFullpage.Wrapper>
                    );
                }}
            />
        );
        return (
            Fullpage()
        );
    }
}

export default About;
